mapper.select:
SELECT FROM xxl_alarm_info AS t
AND t.alarm_type = #{alarmEnum}
AND t.alarm_name LIKE CONCAT(CONCAT('%', #{alarmName}), '%')
AND t.alarm_desc LIKE CONCAT(CONCAT('%', #{alarmDesc}), '%')
ORDER BY id DESC LIMIT #{offset}, #{pagesize}

SELECT COUNT(0) FROM xxl_alarm_info AS t
AND t.alarm_type = #{alarmEnum}
AND t.alarm_name LIKE CONCAT(CONCAT('%', #{alarmName}), '%')
AND t.alarm_desc LIKE CONCAT(CONCAT('%', #{alarmDesc}), '%')
ORDER BY id DESC LIMIT #{offset}, #{pagesize}

SELECT 
FROM xxl_alarm_info AS t
WHERE t.id = #{id}
